mapper.select:
SELECT course.id, course.exam_id, course.course_no, course.course_name, course.course_type, course.is_pub AS pub, course.course_number, course.school_id, course.course_name, course.college_id, course.college_name, course.allow_delay, course.allow_resit, course.room_type, course.duration, course.create_time, course.update_time, course.is_deleted AS deleted, IFNULL(stu.student_count, 0) AS student_count, ecls.teacher_name FROM exam_course course LEFT JOIN (SELECT es.exam_course_id, count(*) AS student_count FROM exam_student es WHERE es.exam_id = #{examId}
AND es.confirmed != 7 AND es.reported != 3
AND es.confirmed = 7
AND es.reported != 3
AND es.reported = 3
GROUP BY es.exam_course_id) stu ON course.id = stu.exam_course_id LEFT JOIN (SELECT exam_course_id, GROUP_CONCAT(DISTINCT teacher_name SEPARATOR '、') AS teacher_name FROM exam_class WHERE exam_id = #{examId} GROUP BY exam_course_id) ecls ON course.id = ecls.exam_course_id WHERE course.exam_id = #{examId}
AND course.confirmed != 7 AND course.reported != 3
AND course.confirmed = 7
AND course.reported != 3
AND course.reported = 3
AND course.college_id = #{collegeId}
AND course.assigned_to = #{assignedTo}
AND course.is_pub = #{pub}
AND course.course_name LIKE concat('%', #{keyword}, '%')
AND course.reported IN
#{item}
AND course.confirmed IN
#{item}
